document.addEventListener("DOMContentLoaded",(()=>{const e=document.getElementById("nav-toggle"),t=document.querySelector("nav");e.addEventListener("click",(o=>{o&&(e.classList.toggle("active"),t.classList.toggle("open"))}));const o=document.querySelectorAll('a[href*="#"]');for(let e of o)e.addEventListener("click",(t=>{if(t.preventDefault(),t){const t=e.getAttribute("href");document.querySelector(""+t).scrollIntoView({behavior:"smooth",block:"start"})}}))}));